mapper.select:
SELECT MAX(ARTICLENUM) 
	FROM COMMUNITYBOARD
WHERE ID=#{value}

SELECT COUNT(*) FROM COMMUNITYREPLY WHERE 
											REF=#{ref} 
										AND DEPTH>#{depth}
										AND STEP>#{step}


										AND	STEP<#{nextStepInSameDepth};

SELECT COUNT(*) 
	FROM COMMUNITYBOARD 
WHERE ARTICLENUM = #{articlenum} AND ID = #{id}

SELECT COUNT(*) 
	FROM COMMUNITYREPLY 
WHERE REPNUM = #{repnum} AND ID = #{id}

SELECT COUNT(*) 
	FROM COMMUNITYBOARDLIKE 
WHERE ARTICLENUM = #{articlenum}

SELECT COUNT(*)
	FROM COMMUNITYREPLYLIKE
WHERE REPNUM = #{repnum}

SELECT COUNT(*) 
	FROM COMMUNITYBOARDLIKE 
WHERE ARTICLENUM = #{articlenum} AND ID = #{id}

SELECT COUNT(*) 
	FROM COMMUNITYREPLYLIKE 
WHERE REPNUM = #{repnum} AND ID = #{id}

SELECT COUNT(*) 
	FROM COMMUNITYBOARDREPORT 
WHERE ARTICLENUM = #{articlenum} AND ID = #{id}

SELECT COUNT(*) 
	FROM COMMUNITYREPLYREPORT 
WHERE REPNUM = #{repnum} AND ID = #{id}

mapper.update:
UPDATE COMMUNITYBOARD SET
	VIEWCOUNT = VIEWCOUNT+1
WHERE ARTICLENUM=#{articlenum}

UPDATE COMMUNITYREPLY SET
	STEP	   = STEP+1
WHERE REF = #{ref} AND STEP > #{step}

UPDATE COMMUNITYBOARD SET
	LIKES = LIKES + 1
WHERE ARTICLENUM = #{articlenum}

UPDATE COMMUNITYBOARD SET
	REPORT = REPORT + 1
WHERE ARTICLENUM = #{articlenum}

UPDATE COMMUNITYREPLY SET
	LIKES = LIKES + 1
WHERE REPNUM = #{repnum}

UPDATE COMMUNITYREPLY SET
	REPORT = REPORT + 1
WHERE REPNUM = #{repnum}

UPDATE COMMUNITYBOARD SET
	LIKES = LIKES - 1
WHERE ARTICLENUM = #{articlenum}

UPDATE COMMUNITYREPLY SET
	LIKES = LIKES - 1
WHERE REPNUM = #{repnum}
